import { createSlice } from "@reduxjs/toolkit";

const wishlistSlice = createSlice({
    name: 'wishlist',
    initialState: {
        list: [],
        count: 0
    },
    reducers: {
        toggleWishlist(state, action) {
            const newItem = action.payload;
            const existingItemIndex = state.list.findIndex(item => item.id === newItem.id);
            const existingItem = state.list[existingItemIndex];
            if (!existingItem) {
                state.list.push({
                    id: newItem.id,
                    name: newItem.name,
                    price: newItem.price,
                    isWishlist: true
                });
            }
            else {
                existingItem.isWishlist = !existingItem.isWishlist;
            }
            state.count = state.list.reduce((countItem, item) => {
                if (item.isWishlist) {
                    countItem++;
                }
                return countItem;
            }, 0);
        },
        checkWishlist(state, action) {
            const newItem = action.payload;
            const existingItemIndex = state.list.findIndex(item => item.id === newItem.id);
        }
    }
});
export const wishlistAction = wishlistSlice.actions;
export default wishlistSlice;
