                                    listProduct.map(i => {
                                        return <ProductItem handleDelete={() => { }} key={i.id} id={i.id} name={i.name} price={i.price} imgUrl={i.images} categories={i.categories} item={i} />
                                    })
                                }
                            </Slider> : <p className="text-center">No items...</p>
                        }

                    </div>
                </div>}

        </div>
    </>
}
export default TrendingProductList;
